import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import "./nav-styles.css";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import TypoGraphy from '@material-ui/core/Typography'

export default function Nav({ initialPosts, posts, setPosts }) {
  const [search, setSearch] = useState("");
  const history = useHistory();

  function filterPosts(searchTerm) {
    if (searchTerm.trim() === "") {
      setPosts(initialPosts);
    } else {
      const filteredPosts = posts.filter(
        (x) =>
          x.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          x.tags.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setPosts(filteredPosts);
    }
  }

  useEffect(() => {
    filterPosts(search);
  }, [search]);

  function resetSearchbar() {
    let searchBar = document.getElementById("searchInput");
    searchBar.value = null;
    setSearch("");
  }

  function readyToSearch() {
    history.push("/");
  }

  return (
    <nav>
      <List component="nav">
            <ListItem component="div">
                <ListItemText inset>
                    <TypoGraphy color="inherit" variant="title">
                        Home
               </TypoGraphy>
                </ListItemText>


                <ListItemText inset>
                    <TypoGraphy color="inherit" variant="title">
                        Posts
               </TypoGraphy>
                </ListItemText>


                <ListItemText inset>
                    <TypoGraphy color="inherit" variant="title">
                        Contact
               </TypoGraphy>
                </ListItemText>
            </ListItem >

        </List>
      <div>
        <ul>
          <li>
            <Link className="navbar" onClick={resetSearchbar} to="/">
              Home
            </Link>
          </li>
          <li>
            <Link className="navbar" onClick={resetSearchbar} to="/newpost">
              New post
            </Link>
          </li>
          <li>
            <label htmlFor="search">Search for post:</label>
            <input
              id="searchInput"
              type="text"
              onChange={(e) => setSearch(e.target.value)}
              onClick={readyToSearch}
            />
          </li>
        </ul>
      </div>
    </nav>
  );
}
