import React from "react";
import { useHistory } from "react-router-dom";
import "./post-styles.css";
import { Grid, Paper, Typography } from "@material-ui/core";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";

export default function Post({ id, url, title, date, onDelete }) {
  const history = useHistory();

  function handleClick() {
    history.push(`/post/${id}`);
  }

  function handleDelete() {
    onDelete(id);
  }

  return (
    <li id="postAndButton">
      <card>
      <div id="postPreview" onClick={handleClick}>
      <CardActionArea>
                <CardMedia
                  component="img"
                  alt="Contemplative Reptile"
                  height="140"
                  image={post.image}
                  title="Contemplative Reptile"
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="h2">
                    {post.title}
                  </Typography>
                  <Typography component="p">{post.excerpt}</Typography>
                </CardContent>
              </CardActionArea>
              <CardActions>
                <Button size="small" color="primary">
                  Share
                </Button>
                <Button size="small" color="primary">
                  Learn More
                </Button>
              </CardActions>




        <img src={url} alt="thumbnail" />
        <h2>{title}</h2>
        <em>{date}</em>
      </div>
      </card>
      <span id="deleteBtn" onClick={handleDelete}>
        <img
          src="https://icons-for-free.com/iconfiles/png/512/close+closecancelsquare+cross+delete+remove+square+icon-1320185734508070818.png"
          alt="Delete post"
        />
      </span>
    </li>
  );
}
