import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Post from "../../components/Post/Post";
import { initialPosts } from "../../initialPosts";
import EditPost from "../EditPost/EditPost"
import "./postdetails-styles.css";

export default function PostDetails() {
  const [post, setPost] = useState(null);
  const [edit, setEdit] = useState(false);
  let { id } = useParams();

  const editposts = () => {
     setEdit(true);
     console.log("test");
     return (
      <div>
        {edit && 
        <EditPost id ={post.id} title ={post.title} date = {post.date} text = {post.text} />
      }
      </div>
     );
  };

  useEffect(() => {
    const viewPost = initialPosts.find((post) => post.id === parseInt(id, 10));
    setPost(viewPost);
  }, [id]);



  return post ? (
    <div id="viewPostWrapper">
       <button
         onClick={editposts}
         style={{alignContent:"right",  fontFamily:"cursive", backgroundColor:"white"}}
        >
           <Link className="navbar" onClick={resetSearchbar} to="/newpost" style={{alignContent:"center",  fontFamily:"cursive" }}>
              New post
            </Link>
             Edit Post 
        </button>
      <h2 style={{alignContent:"center",  fontFamily:"cursive", color:"black"}}>{post.title}</h2>
      <em style={{alignContent:"center",  fontFamily:"cursive", color:"black"}}>{post.date} </em>
      <div>
        <br/>
      <img src={post.url} alt="thumbnail"/>
        <br/>
      </div>
      <p style={{alignContent:"center",  fontFamily:"cursive", color:"black"}}>{post.postText}</p>
    </div>
  ) : null;
}
